using NDatabase.Api;

namespace NDatabase.Btree
{
    /// <summary>
    ///   The NDatabase ODB BTree.
    /// </summary>
    /// <remarks>
    ///   The NDatabase ODB BTree. It extends the DefaultBTree implementation to add the ODB OID generated by the ODB database.
    /// </remarks>
    internal sealed class OdbBtreeSingle : BTreeSingleValuePerKey
    {
        private OID _oid;

        public OdbBtreeSingle(int degree, IBTreePersister persister) : base(degree, persister)
        {
        }

        public override IBTreeNode BuildNode()
        {
            return new OdbBtreeNodeSingle(this);
        }

        public override object GetId()
        {
            return _oid;
        }

        public override void SetId(object id)
        {
            _oid = (OID) id;
        }
    }
}
